package com.javaex.practice03;

public class Ex05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
					//유심히 보기//
		int i =1;
		boolean flag =true;
		
		while(flag) {
			if(i>=5) {
				flag= false;
				
			}
		
			System.out.println(i);
			i=i+1;
		
		}
		
		i=1;
		
		while(true) {
			if(i>=5) {
				break;
				
			}
		
			System.out.println(i);
			i=i+1;
		
		}
		
		
	}

}
